import React from 'react'
import { useBoardCreate } from '../queries/useBoardQueries';

const BoardCreate = ({data,setUpdateForm}) => {

  const { mutate:createBoard, mutate : useBoardUpdate ,isLoading, isSuccess, isError } = useBoardCreate();


  const [formData, setFormData] = React.useState({
    idx : data?.idx || '',
    title : data?.title ||'',
    content : data?.content || '',
    createWriter : data?.createWriter || '',
    updateWriter : data?.updateWriter || '',
  });

  React.useEffect(() => {
    setFormData({
      idx: data?.idx || '',
      title: data?.title || '',
      content: data?.content || '',
      createWriter: data?.createWriter || '',
      updateWriter : data?.updateWriter || '',
    });
  }, [data]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name] : e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.idx) {
      createBoard(formData);
    } else {
      useBoardUpdate(formData)
    }

  };


  return (
    <div style={{marginTop:'30px'}}>
      <form onSubmit={handleSubmit}>
        <label>
          제목:
          <input type="text" name="title" value={formData.title} onChange={handleChange} />
        </label>
        <label>
          내용:
          <textarea name="content" value={formData.content} onChange={handleChange} />
        </label>
        {
          formData.idx ?
          <label> 수정자:
            <input type="text" name="updateWriter" value={formData.updateWriter} onChange={handleChange} />
          </label> :
          <label>
            작성자:
            <input type="text" name="createWriter" value={formData.createWriter} onChange={handleChange} />
          </label>
        }
        <button>등록</button>
      </form>
      {isLoading && <div>게시글을 작성하는 중...</div>}
      {isSuccess && <div>게시글이 성공적으로 작성되었습니다!</div>}
      {isError && <div>게시글 작성 중 오류가 발생했습니다: {isError.message}</div>}
    </div>
  )
}

export default BoardCreate
