import { useQuery } from "@tanstack/react-query";
import BOARD_API from "../api/Board";


// 게시물  리스트
export const useBoardList = () => {
  return useQuery({
    queryKey: ["boardList"],
    queryFn: () => {
      return BOARD_API.boardList();
    }
  })
};

// 게시물  상세 
export const useBoardDetail = (idx) => {
  return useQuery({
    queryKey: ["board",idx],
    queryFn: () => {
      return BOARD_API.board(idx);
    }
  })
};
