import { SERVER_URL } from "../env"


const BOARD_API = {
  boardList : async() => {
    const res = await fetch(`${SERVER_URL}/boardList`);

    if(!res.ok){
      console.error("[ boardList ] : error");
      throw new Error('Network ERROR');
    };
    return res.json();    
  },
  board : async(idx) => {

    const baseURL = `${SERVER_URL}/boardDetail`;
    const params = {
      idx
    };

    const queryString = new URLSearchParams(params).toString();
    const resultURL = `${baseURL}?${queryString}`

    const res = await fetch(resultURL,
      {
        method : 'GET',
        headers : {
          'Content-Type' : 'application/json'
        }
      });

    if(!res.ok){
      console.error("[ board ] : error");
      throw new Error('Network ERROR');
    };
    return res.json();
  },

};



export default BOARD_API;
