import create from 'zustand';
import { persist, devtools } from 'zustand/middleware';

let usersStore = (set) => ({
  selectedUserId: null,
  setSelectedUserId: (id) => set({ selectedUserId: id }),
});

// Apply devtools and persist middleware
usersStore = devtools(usersStore); // 디버깅 가능하게
// usersStore = persist(usersStore, { name: 'userDataStore' }); // 로컬에저장하는거

export default create(usersStore);
