import { SERVER_URL } from "../env"


const BOARD_API = {
  boardList : async() => {
    const res = await fetch(`${SERVER_URL}/boardList`);

    if(!res.ok){
      console.error("[ boardList ] : error");
      throw new Error('Network ERROR');
    };
    return res.json();    
  },
  board : async(idx) => {
    const res = await fetch(`${SERVER_URL}/boardDetail`);

    if(!res.ok){
      console.error("[ board ] : error");
      throw new Error('Network ERROR');
    };
    return res.json();
  },

};



export default BOARD_API;
