import create from 'zustand';
import { persist, devtools } from 'zustand/middleware';

let usersStore = (set) => ({
  userData: [],
  setUserData: (data) => set({ userData: data }),
  selectedUserId: null,
  setSelectedUserId: (id) => set({ selectedUserId: id }),
});

// Apply devtools and persist middleware
usersStore = devtools(usersStore); // Enable Zustand devtools
usersStore = persist(usersStore, { name: 'userDataStore' }); // Persist userData to localStorage

export default create(usersStore);
