// src/store/useBoardStore.js
import create from 'zustand';
import { devtools } from 'zustand/middleware';

const useBoardStore = create(
  devtools((set) => ({
    selectBoardIdx: null,
    setSelectBoardIdx: (idx) => set({ selectBoardIdx: idx }),
  }))
);

export default useBoardStore;
