import { queryString } from "../common/QueryString";
import { SERVER_URL } from "../env"


const BOARD_API = {
  boardList : async() => {

    const baseURL = `${SERVER_URL}/boardList`;
    const params = {
    };

    const res = await fetch(queryString(baseURL,params));

    if(!res.ok){
      console.error("[ boardList ] : error");
      throw new Error('Network ERROR');
    };
    return res.json();    
  },
  board : async(idx) => {

    const baseURL = `${SERVER_URL}/boardDetail`;
    const params = {
      idx
    };

    const res = await fetch(queryString(baseURL,params),
      {
        method : 'GET',
        headers : {
          'Content-Type' : 'application/json'
        }
      });

    if(!res.ok){
      console.error("[ board ] : error");
      throw new Error('Network ERROR');
    };
    return res.json();
  },

};



export default BOARD_API;
